namespace Amici\BannerSlider\Model\ResourceModel\Slider;

use Amici\BannerSlider\Api\Data\SliderInterface;
use \Amici\BannerSlider\Model\ResourceModel\AbstractCollection;
use Amici\BannerSlider\Model\Slider;
use Amici\BannerSlider\Model\ResourceModel\Slider as SliderResourceModel;

/**
 * Class Collection
 * @package Amici\BannerSlider\Model\ResourceModel\Slider
 */
class Collection extends AbstractCollection
{
    /**
     * @var string
     */
    protected $_idFieldName = 'slider_id';

    /**
     * Load data for preview flag
     *
     * @var bool
     */
    protected $_previewFlag;

    /**
     * Event prefix
     *
     * @var string
     */
    protected $_eventPrefix = 'amici_slider_collection';

    /**
     * Event object
     *
     * @var string
     */
    protected $_eventObject = 'slider_collection';

    /**
     * Define resource model
     *
     * @return void
     */
    protected function _construct()
    {
        $this->_init(Slider::class, SliderResourceModel::class);
        $this->_map['fields']['slider_id'] = 'main_table.slider_id';
        $this->_map['fields']['store'] = 'store_table.store_id';
    }

    /**
     * Set first store flag
     *
     * @param bool $flag
     * @return $this
     */
    public function setFirstStoreFlag($flag = false)
    {
        $this->_previewFlag = $flag;
        return $this;
    }

    /**
     * Add filter by store
     *
     * @param int|array|\Magento\Store\Model\Store $store
     * @param bool $withAdmin
     * @return $this
     */
    public function addStoreFilter($store, $withAdmin = true)
    {
        if (!$this->getFlag('store_filter_added')) {
            $this->performAddStoreFilter($store, $withAdmin);
        }
        return $this;
    }

    /**
     * Perform operations after collection load
     *
     * @return $this
     */
    protected function _afterLoad()
    {
        $entityMetadata = $this->metadataPool->getMetadata(SliderInterface::class);
        $this->performAfterLoad('amici_bannerslider_slider_store', $entityMetadata->getLinkField());
        $this->_previewFlag = false;
        
        return parent::_afterLoad();
    }

    /**
     * Perform operations before rendering filters
     *
     * @return void
     */
    protected function _renderFiltersBefore()
    {
        $entityMetadata = $this->metadataPool->getMetadata(SliderInterface::class);
        $this->joinStoreRelationTable('amici_bannerslider_slider_store', $entityMetadata->getLinkField());
    }
}
